[{"location":"polynomial/#Polynomial-bases","page":"Polynomial bases","title":"Polynomial bases","text":"","category":"section"},{"location":"polynomial/","page":"Polynomial bases","title":"Polynomial bases","text":"using HelFEM, Plots\nlet xs = range(-1, 1, length=2001)\n    a = @animate for nnodes = 2:15\n        b = HelFEM.PolynomialBasis(:legendre, nnodes)\n        ys = b(xs)\n        plot(xs, ys, label=false, title=\"Legendre, nnodes=$nnodes\")\n    end\n    gif(a, fps=0.5)\nend","category":"page"},{"location":"polynomial/","page":"Polynomial bases","title":"Polynomial bases","text":"using HelFEM, Plots\nlet xs = range(-1, 1, length=2001)\n    a = @animate for nnodes = 2:15\n        b = HelFEM.PolynomialBasis(:hermite, nnodes)\n        ys = b(xs)\n        plot(xs, ys, label=false, title=\"Hermite, nnodes=$nnodes\")\n    end\n    gif(a, fps=0.5)\nend","category":"page"},{"location":"polynomial/","page":"Polynomial bases","title":"Polynomial bases","text":"using HelFEM, Plots\nlet xs = range(-1, 1, length=2001)\n    a = @animate for nnodes = 2:15\n        b = HelFEM.PolynomialBasis(:lip, nnodes)\n        ys = b(xs)\n        plot(xs, ys, label=false, title=\"LIP, nnodes=$nnodes\")\n    end\n    gif(a, fps=0.5)\nend","category":"page"},{"location":"radialbasis/#RadialBasis","page":"RadialBasis","title":"RadialBasis","text":"","category":"section"},{"location":"radialbasis/","page":"RadialBasis","title":"RadialBasis","text":"using HelFEM, Plots\npb = HelFEM.PolynomialBasis(:lip, 4)\nxgrid = [0, 1, 2, 3]\nb = HelFEM.RadialBasis(pb, xgrid, 100)\n# Calculate points for plotting\nrs = range(minimum(xgrid), maximum(xgrid), length=501)\nbs = b(rs)\na = @animate for i in 1:length(b)\n    plot(layout=(2,1), size=(900, 700), legend=false,\n        ylabel = [raw\"$b(r)$\" raw\"$r~b(r)$\"]\n    )\n    plot!(rs, bs, c=:lightgray, subplot=1)\n    plot!(rs, bs[:,i], c=:black, subplot=1)\n    plot!(rs, bs .* rs, c=:lightgray, subplot=2)\n    plot!(rs, bs[:,i] .* rs, c=:black, subplot=2)\n    vline!([xgrid xgrid], ls=:dash, c=1)\nend\ngif(a, fps=1)","category":"page"},{"location":"#HelFEM.jl","page":"Overview","title":"HelFEM.jl","text":"","category":"section"},{"location":"","page":"Overview","title":"Overview","text":"HelFEM.jl is a Julia wrapper for the HelFEM finite element method library.","category":"page"}]
